import React from 'react';
import { useStompClient } from 'react-stomp-hooks';

import './player.css';

function Player(props) {
  const { localPlayerCards, name, coins, cardNumbers } = props;
  let card0 = localPlayerCards[0];
  let card1 = localPlayerCards[1];

  const stompClient = useStompClient();
  const [subscription, setSubscription] = useState("/user/lobby");
  const lobbyName = Props.lobbyName;
  const [headers, setHeaders] = useState({"lobbyName":lobbyName}); //JSON stringfy 하면 안됨.
  const [body, setBody] = useState("");
  const publishMessage = (body) => {
    stompClient.publish({ subscription, destination: '/app/game', headers: { lobbyName: lobbyName }, body });
  };

  return (
    <div>
      {/* <p>{name}</p> */}
      <div>
        <img src={require(""+`./images/${card0}.png`)} alt={card0} className="img cardImg"/>
        <img src={require(""+`./images/${card1}.png`)} alt={card0} className="img cardImg"/>
      </div>
      {/* <div>
        <div className='coin img'></div><span>{coins}</span>
      </div> */}
    </div>
  );
}

export default Player;
