import React, {useState} from 'react';
import {useStompClient } from 'react-stomp-hooks';

function BlockConsole({ destination, lobbyName, body, blockedMessages }) {

  const stompClient = useStompClient();
  const [subscription, setSubscription] = useState("/user/lobby");

  const publishMessage = (body) => {
    stompClient.publish({ subscription, destination, headers: { lobbyName: lobbyName }, body });
  };

  // "{"type":"CHOICE","userMessage":"액션을 고르시오.","content":["Income","ForeignAid","Tax","Assassinate","Exchange","Steal"]}"

  const handleIncomeClick = () => {
    publishMessage("Income");
  };

  const handleClick = (msg) => {
    const message = { body: JSON.stringify({ message: msg }) };
    console.log(message);
    publishMessage(message);

  };

  return (
    <div>
      {blockedMessages.map((msg, index) => (
        <button key={index} onClick={() => handleClick(msg)}>
          {msg}
        </button>
      ))}
    </div>
  );
}

export default BlockConsole;
