import React, { useState, useEffect, useRef } from 'react';
import StompHook from './StompHook';
import { useLocation } from 'react-router-dom';
import Banker from '../front/banker';
import Console from '../front/console';
import Player from '../front/player';

import './StompHookProps.css';

function StompHookProps() {

  // const location = useLocation();

  // const { destination, headers } = location.state;
  // const lobbyName = headers.lobbyName;
  const [destination, setDestination] = useState("/app/create");
  const [lobbyName, setLobbyName] = useState("test");
  const [headers, setHeaders] = useState({lobbyName: lobbyName});
  const subscription = "/user/lobby";
  const body = "Income";
  const [receivedMessage, setReceivedMessage] = useState("");
  const [latestUpdateMessage, setLatestUpdateMessage] = useState("");
  const messageRef = useRef([]);

  const handleReceivedMessage = (message) => {
    setReceivedMessage(message);
  };

  const updateMessageRef = useRef([]);

  useEffect(() => {
    messageRef.current.push(receivedMessage); // useRef로 저장
    const updateMessages = messageRef.current.filter((msg) => {
      // {로 시작하고 }로 끝나는 문자열 중에서 type이 UPDATE인 것만 필터링
      if (msg.startsWith("{") && msg.endsWith("}")) {
        const parsedMsg = JSON.parse(msg);
        return parsedMsg.type === "UPDATE";
      } else {
        return false;
      }
    });
    console.log(updateMessages);
    // 이전 값들을 모두 복사한 후, 새로운 값 추가
    console.log(updateMessageRef);
    updateMessageRef.current = [...updateMessageRef.current, updateMessages];
    console.log(updateMessageRef);
    // 이전 값들을 모두 삭제
    messageRef.current = [];
  }, [receivedMessage]);


  return ( 
    <div>
        <StompHook
          subscription={subscription}
          destination={destination}
          headers={headers}
          body={body}
          onReceivedMessage={handleReceivedMessage}
          lobbyName={lobbyName}
        />
        <div className="BankerConsole">
          <Banker receivedMessage={receivedMessage} />
          <Console destination={destination} lobbyName={lobbyName} body={body} />

        </div>

        {/* {updateMessageRef.current[0] && (
          <div>
            {updateMessageRef.current[0].content.players.name.map((msg, index) => (
            <Player name={JSON.parse(updateMessageRef.current[0]).content.players[0].name} coins={JSON.parse(updateMessageRef.current[0]).content.players[0].coins} cardNumbers={JSON.parse(updateMessageRef.current[0]).content.players[0].cardNumbers} />
            ))}
          </div>
        )} */}

        {/* 
        <div>
          {updateMessageRef.current.map((msg, index) => (
        <Player key={index} name={updateMessageRef.current.content.players[0].name} coins={updateMessageRef.current.content.players[0].coins} cardNumbers={updateMessageRef.current.content.players[0].cardNumbers} />
          ))}
        </div> */}
    </div>
  );
}

export default StompHookProps;
