import React, { useState } from 'react';
import { useSubscription, useStompClient } from 'react-stomp-hooks';

function StompHook({ subscription, destination, headers, body, onReceivedMessage }) {
  const stompClient = useStompClient();

  useSubscription(subscription, (str) => {
    console.log(str.body);
    // const object = JSON.parse(str.body);
    setReceivedMessage(str.body);
  });

  function handleSubmit(event) {
    event.preventDefault();

    if (stompClient) {
      stompClient.publish({
        destination,
        headers,
        body,
      });
    } else {
      console.log("stompClient is null");
    } // 통신이 안되면.
  }

  return (
<div>
      <form onSubmit={handleSubmit}>
        {headers.map((header, index) => (
          <div key={index}>
            <label>
              <input
                type="radio"
                value={body[index]}
                checked={selectedOption === body[index]}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              {Object.values(header)}
            </label>
          </div>
        ))}
        <button type="submit">서버로 메세지 보내기(통신)</button>
      </form>
      <p>Selected option: {selectedOption}</p>
    </div>
  );
}

export default StompHook;
