//StompHook.js
import React, { useState } from 'react';
import { useSubscription, useStompClient } from 'react-stomp-hooks';

function StompHook({ subscription, destination, headers, body, onReceivedMessage }) {
  const stompClient = useStompClient();
  const [receivedMessage, setReceivedMessage] = useState("");

  const options = [
    { value: "Income", label: "Income" },
    { value: "Tax", label: "Tax" },
    { value: "Bonus", label: "Bonus" },
  ];
  const [selectedOption, setSelectedOption] = useState(options[0].value);

  useSubscription(subscription, (str) => {
    console.log(str.body);
    // const object = JSON.parse(str.body);
    onReceivedMessage(str.body);
  });

  function handleSubmit(event) {
    event.preventDefault();

    if (stompClient) {
      stompClient.publish({
        destination,
        // headers: {...headers, lobbyName: selectedOption},
        headers,
        body : {selectedOption},
      });
    } else {
      console.log("stompClient is null");
    }
  }

  return (
<div>
      <form onSubmit={handleSubmit}>
        {options.map((option) => (
          <label key={option.value}>
            <input
              type="radio"
              name="options"
              value={option.value}
              checked={selectedOption === option.value}
              onChange={(e) => setSelectedOption(e.target.value)}
            />
            {option.label}
          </label>
        ))}
        <button type="submit">서버로 메세지 보내기(통신)</button>
      </form>
      <p>Received message: {receivedMessage}</p>
    </div>
  );
}

export default StompHook;
