//Banker.js
import React, { useState, useEffect } from 'react';

function Banker(props) {
  const { receivedMessage } = props;
  const [localPlayerCards, setLocalPlayerCards] = useState([]);

  useEffect((message) => {
    const messageObj = JSON.parse(message);

    if (messageObj.type === 'UPDATE') {
      const content = messageObj.content;
      const localCards = content.localPlayerCards;

      if (localCards && localCards.length > 0) {
        setLocalPlayerCards(localCards);
      }
    }
  });

  return (
    <div className="banker">
      <p className="announcement anc-message">
        {receivedMessage ? receivedMessage : "다른 플레이어의 차례를 기다리고 있습니다."}
      </p>  
      {localPlayerCards.length > 0 &&
        <div>
          <p>Local Player Cards:</p>
          <ul>
            {localPlayerCards.map(card => (
              <li key={card}>{card}</li>
            ))}
          </ul>
        </div>
      }
    </div>
  );
}

export default Banker;
