//StompHook.js
import React, { useState } from 'react';
import { useSubscription, useStompClient } from 'react-stomp-hooks';

function StompHook(onReceivedMessage) {
  const stompClient = useStompClient();
  const [receivedMessage, setReceivedMessage] = useState("");
  const [subscription, setSubscription] = useState("/user/lobby");
  const [destination, setDestination] = useState("/app/create");
  const [headers, setHeaders] = useState({ lobbyName: "Room1" });
  const [body, setBody] = useState("");

  const options = [
    { value: "Income", label: "Income" },
    { value: "Tax", label: "Tax" },
    { value: "Bonus", label: "Bonus" },
  ];

  const [selectedOption, setSelectedOption] = useState(options[0].value);

  useSubscription(subscription, (str) => {
    console.log(str.body);
    onReceivedMessage(str.body);
  });

  function handleSubmit(event) {
    event.preventDefault();

    if (stompClient) {
      stompClient.publish({
        destination,
        headers: {...headers, lobbyName: selectedOption},
        body,
      });
    } else {
      console.log("stompClient is null");
    }
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div>
          <span>subscription:</span>
          <input
            type="text"
            value={subscription}
            onChange={(e) => setSubscription(e.target.value)}
          />
        </div>
        <div>
          <span>destination:</span>
          <input
            type="text"
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
          />
        </div>
        <div>
          <span>headers:</span>
          <input
            type="text"
            value={headers}
            onChange={(e) => setHeaders(e.target.value)}
          />
        </div>
        <div>
          <span>body:</span>
          {options.map((option) => (
            <label key={option.value}>
              <input
                type="radio"
                name="options"
                value={option.value}
                checked={selectedOption === option.value}
                onChange={(e) => setSelectedOption(e.target.value)}
              />
              {option.label}
            </label>
          ))}
        </div>
        <button type="submit">서버로 메세지 보내기(통신)</button>
      </form>
      <p>Received message: {receivedMessage}</p>
    </div>
  );
}

export default StompHook;
