import React, { useState, useEffect } from 'react';
import StompHook from './StompHook';
import { StompSessionProvider, useStompClient } from 'react-stomp-hooks';
import { useLocation } from 'react-router-dom';
import Banker from '../front/banker';
import Console from '../front/console';
import Player from '../front/player';

import './StompHookProps.css';

function StompHookProps() {

  const stompClient = useStompClient();

  const location = useLocation();

  const { destination, headers } = location.state;
  const lobbyName = headers.lobbyName;
  const subscription = "/user/lobby";
  const body = "Income";
  const [receivedMessage, setReceivedMessage] = useState("");

  const handleReceivedMessage = (message) => {
    setReceivedMessage(message);
  };

  useEffect(() => {
    if (stompClient) {
      stompClient.publish({
        destination,
        headers,
        body,
      });
      console.log(headers);
    } else {
      console.log("stompClient is null");
    }

    if (typeof receivedMessage === 'string' && receivedMessage.trim().startsWith('{') && receivedMessage.trim().endsWith('}')) {
      const parsedMessage = JSON.parse(receivedMessage);
      if (parsedMessage.type === 'UPDATE') {
        const { userMessage, content } = parsedMessage;
        console.log(userMessage, content);
        console.log(JSON.parse(receivedMessage).content.players);
      } 
    }
  }, [receivedMessage]);

  return (
    <div>
      <StompSessionProvider url="ws://javaspringbootcoupgamebackend-env.eba-2u3en2tr.ap-northeast-2.elasticbeanstalk.com/ws">
        <StompHook
          subscription={subscription}
          destination={destination}
          headers={headers}
          body={body}
          onReceivedMessage={handleReceivedMessage}
          lobbyName={lobbyName}
        />
        <div className="BankerConsole">
          <Banker receivedMessage={receivedMessage} />
          <Console destination={destination} body={body} />
        </div>
        {receivedMessage !== "" && typeof receivedMessage === 'string' && receivedMessage.trim().startsWith('{') && receivedMessage.trim().endsWith('}') && JSON.parse(receivedMessage).type === 'UPDATE' &&
          <div>
            {JSON.parse(receivedMessage).content.players.map(player => (
              <Player key={player.name} name={player.name} coins={player.coins} cardNumbers={player.cardNumbers} />
            ))}
          </div>
        }
      </StompSessionProvider>
    </div>
  );
}

export default StompHookProps;
