//StompHook.js
import React, { useState, useEffect } from 'react';
import { useSubscription, useStompClient } from 'react-stomp-hooks';

function StompHook(props) {
  const { onReceivedMessage } = props;
  const [destination, setDestination] = useState(props.destination);
  const stompClient = useStompClient();
  const [receivedMessage, setReceivedMessage] = useState("");
  const [subscription, setSubscription] = useState("/user/lobby");
  const [lobbyName, setLobbyName] = useState("Room1");
  const [headers, setHeaders] = useState({"lobbyName":lobbyName}); //JSON stringfy 하면 안됨.
  const [body, setBody] = useState("");

  useEffect(() => {
    setHeaders({ "lobbyName": lobbyName });
  }, [lobbyName]);

  useSubscription(subscription, (str) => {
    console.log(str.body);
    onReceivedMessage(str.body);
  });

  function handleSubmit(event) {
    event.preventDefault();

    if (stompClient) {
      stompClient.publish({
        destination,
        headers,
        body,
      });
      console.log(headers);
    } else {
      console.log("stompClient is null");
    }
  }

  return (
    <div style={{backgroundColor: "pink", paddingLeft: "300px"}}>
      <form onSubmit={handleSubmit}>
        <div>
          <span>subscription:</span>
          <input
            type="text"
            value={subscription}
            onChange={(e) => setSubscription(e.target.value)}
          />
        </div>
        <div>
          <span>destination:</span>
          <input
            type="text"
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
          />
        </div>
        <div>
          <span>headers:</span>
          <input
            type="text"
            value={props.lobbyName}
            onChange={(e) => setLobbyName(e.target.value)}
          />
        </div>
        <button type="submit">서버로 메세지 보내기(통신)</button>
      </form>
    </div>
  );
}

export default StompHook;
